import React from "react";
import { useForm } from "react-hook-form";
import { useState, useEffect } from "react";
import ReactDOM from "react-dom";



const Resume_skill = (props) => {
  let id = props.match.params.id;
const { register, handleSubmit, reset } = useForm()     
const [resume, setResume] = useState({
  personal_info : [],
  skills: [],
  qualifications: [],
  work_experience: [],
  education: [],
  training: []
});

        const [values, setValues] = useState({
          name: "",
          phone: "",
          email: "",
          street_address: "",
          country: "",
          province: "",
          city: ""
        })
        useEffect(() => {
          async function fetchData() {
          const res = await fetch("http://localhost:3008/resume");
          res.json().then((res) => setResume(res));
            //.catch((err) => setErrors(err));
          }
          fetchData();
      }, []);
      const data = resume.skills[0]
    useEffect(() => {
      if(data) {
        setValues({
          skill: data.skill
        })
      }
    }, [data])
// If field not touched submit returns empty value


handleSubmit(async (data) => await
    fetch(`http://localhost:3008//resume/skill/${id}`, {
    method: "put",
    headers: {
      Accept: "application/json",
      "Content-Type": "application/json",
    },

    //make sure to serialize your JSON body
    body: JSON.stringify(data),
  }).then((response) => response.json())

)

const onSubmit = async data => {
    await data;
    return data
    //reset();



  };
    return (
        <form onSubmit={handleSubmit(onSubmit)}>

        <input {...register("skill")} Value={values.skill}/>


        <input type="submit" />
      </form>
    );
}

export default Resume_skill
