import React from "react";
import { useForm, Controller } from "react-hook-form";
import { useState, useEffect } from "react";
import ReactDOM from "react-dom";

//import "./styles.css";

const Resume_crud_1 = () => {

 const [resume, setResume] = useState({
            personal_info : [],
            skills: [],
            qualifications: [],
            work_experience: [],
            education: [],
            training: []
        });  

    const { register, handleSubmit } = useForm()

        const [values, setValues] = useState({
          name: "",
          phone: "",
          email: "",
          street_address: "",
          country: "",
          province: "",
          city: ""
        })



          useEffect(() => {
            async function fetchData() {
              const res = await fetch("http://localhost:3008/resume");
              res.json().then((res) => setResume(res));

              //.catch((err) => setErrors(err));
            }
            fetchData();
          }, [resume]);

          console.log(resume)

      useEffect(() => {
        if(resume) {
          setValues({
            name: resume.personal_info[0].name,
          })
        }
      }, [resume])

//console.log(resume)


      //console.log(resume)
      const onSubmit = data => console.log(data);
    return (
        <form onSubmit={handleSubmit(onSubmit)}>

        <input {...register("name")} defaultValue={values.name}/>
        <input {...register("phone")} />
        <input {...register("email")} />
        <input {...register("street_address")} />
        <input {...register("city")} />
        <input {...register("province")} />
        <input {...register("country")} />

       {/* {errors.exampleRequired && <span>This field is required</span>} */}

        <input type="submit" />
      </form>
    );
}

export default Resume_crud_1
